const tagHtml=document.querySelector("html");const btnTheme=document.querySelector("#button_theme");let checkTheme=true;btnTheme.addEventListener("click",(()=>{let isDarkMode=localStorage.getItem("darkMode")==="true";isDarkMode=!isDarkMode;if(isDarkMode){btnTheme.querySelector("span").textContent="nights_stay";tagHtml.classList.add("dark")}else{btnTheme.querySelector("span").textContent="brightness_6";tagHtml.classList.remove("dark")}localStorage.setItem("darkMode",isDarkMode.toString())}));window.onload=function(){checkDarkMode()};function checkDarkMode(){const isDarkMode=localStorage.getItem("darkMode")==="true";if(isDarkMode){tagHtml.classList.add("dark");btnTheme.querySelector("span").textContent="nights_stay"}else{tagHtml.classList.remove("dark");btnTheme.querySelector("span").textContent="brightness_6"}}